('00000000-0000-0000-0010-100000000000', '00000000-0000-0000-0010-100000000000', '00000000-0000-0000-0013-400000000000', 'Sportaktivität', 'Eine Sportaktivität bei der alle mitmachen können'),
('00000000-0000-0000-0010-200000000000', '00000000-0000-0000-0010-200000000000', '00000000-0000-0000-0013-400000000000', 'Chillen unter der Woche', 'Wir chillen sau gern unter der Woche, vor allem wenns draußen so kalt ist'),
('00000000-0000-0000-0010-300000000000', '00000000-0000-0000-0010-300000000000', '00000000-0000-0000-0013-400000000000', 'Music gathering', 'Bring dein Instrument mit und dann wird gejammt!'),
('00000000-0000-0000-0010-400000000000', '00000000-0000-0000-0010-400000000000', '00000000-0000-0000-0013-400000000000', 'Gaming am Wochenende', 'Am Wochenende wird zusammen gezockt. Alles ist erlaubt.'),
('00000000-0000-0000-0010-500000000000', '00000000-0000-0000-0010-500000000000', '00000000-0000-0000-0013-400000000000', 'Tag der offenen Tür', 'Wir veranstalten einen Tag der offenen Tür und jeder ist herzlich eingeladen. Es gibt Essen und zu trinken!'),
('00000000-0000-0000-0010-600000000000', '00000000-0000-0000-0010-600000000000', '00000000-0000-0000-0013-400000000000', 'Selbsthilfegruppe', 'Wir wollen euch bei bringen wir ihr euch am besten selbst hilft und wie man Informationen aus dem Netz ziehen kann.'),
('00000000-0000-0000-0010-700000000000', '00000000-0000-0000-0010-700000000000', '00000000-0000-0000-0013-400000000000', 'Ausflug ins Fantasialand', 'Wir machen einen Ausflug ins Fantasialand. Achtung, es gibt nur begränzt Plätze!'),
('00000000-0000-0000-0010-800000000000', '00000000-0000-0000-0010-800000000000', '00000000-0000-0000-0013-400000000000', 'Jugendversammlung der Partei XY', 'Wir von der Partei YX wollen eine neue Jugendorganisation in Wuppertal gründen und laden hiermit jeden herzlich ein zu kommen.'),
('00000000-0000-0000-0010-900000000000', '00000000-0000-0000-0010-900000000000', '00000000-0000-0000-0013-400000000000', 'Hackathon', 'Wir wollen zusammen ein Portal basteln, das für Jugendliche aus Wuppertal genutzt werden soll.'),
('00000000-0000-0000-0010-110000000000', '00000000-0000-0000-0010-110000000000', '00000000-0000-0000-0013-400000000000', 'Sportlern will gelernt sein', 'So ist es!'),
('00000000-0000-0000-0010-120000000000', '00000000-0000-0000-0010-120000000000', '00000000-0000-0000-0013-400000000000', 'Theatergruppe', 'Wer schon mal Lust auf Theaterimprovisation hatte ist bei uns richtig.'),
('00000000-0000-0000-0010-130000000000', '00000000-0000-0000-0010-130000000000', '00000000-0000-0000-0013-400000000000', 'Einsteigerkurs Gitarre', 'Wer Lust hat, kann beim Schnupperkurs mit machen. Wir haben nur begränzt Gitarren und wer eine hat, darf sie gerne mitbringen'),
('00000000-0000-0000-0010-140000000000', '00000000-0000-0000-0010-140000000000', '00000000-0000-0000-0013-400000000000', 'Retro zocken', 'Von Mario bis Sonic wollen wir alle Retroklassiker auspacken und zusammen zocken.'),
('00000000-0000-0000-0010-150000000000', '00000000-0000-0000-0010-150000000000', '00000000-0000-0000-0013-400000000000', 'Treffpunkt Religion', 'Für Interessierte ein Treffpunkt zum Austausch'),
('00000000-0000-0000-0010-160000000000', '00000000-0000-0000-0010-160000000000', '00000000-0000-0000-0013-400000000000', 'Kennenlernen', 'Was man eben beim kennen lernen so macht :-)'),
('00000000-0000-0000-0010-170000000000', '00000000-0000-0000-0010-170000000000', '00000000-0000-0000-0013-400000000000', 'Ausflug nach Köln', 'Wir machen einen Ausflug nach Kölle. Achtung, es gibt nur begränzt Plätze!'),
('00000000-0000-0000-0010-180000000000', '00000000-0000-0000-0010-180000000000', '00000000-0000-0000-0013-400000000000', 'Wahlparty Jugendratswahl', 'Wir wollen nach der Wahl etwas feiern.'),
('00000000-0000-0000-0010-190000000000', '00000000-0000-0000-0010-190000000000', '00000000-0000-0000-0013-400000000000', 'Launchparty Wuppngo', 'Kommt vorbei wenn das Wuppngo offiziell online geht'),
('00000000-0000-0000-0010-210000000000', '00000000-0000-0000-0010-210000000000', '00000000-0000-0000-0013-400000000000', 'Stadtrundlauf durch Wuppertal', 'Wir wollen euch Stadtteile und Orte in Wuppertal zeigen, die ihr selbst nicht mal kennt.'),
('00000000-0000-0000-0010-220000000000', '00000000-0000-0000-0010-220000000000', '00000000-0000-0000-0013-400000000000', 'Chor zum Mitmachen', 'Egal, ob die Stimme gut oder schlecht ist. Wir finden für jeden einen Platz im Chor.'),
('00000000-0000-0000-0010-230000000000', '00000000-0000-0000-0010-230000000000', '00000000-0000-0000-0013-400000000000', 'Chillen und zocken', 'Genau das: chillen und zocken.');
